import React, { Component } from 'react';
import Popover from 'popover';
import OverlayTrigger from 'react-bootstrap';

const ItemDetail = props => {
    const { name, price, amount, imageUrl } = props.item
    const baseApiUrl = 'http://localhost:3000';

    var imageUrlFull = baseApiUrl.concat(imageUrl);

    return (
        <li className="media">
            <div className="media-left">
                <img
                    className="media-object img-circle"
                    width="150px"
                    src={imageUrlFull}
                    alt={imageUrl}
                />
            </div>
            <div className="media-right">
                <h4 className="media-heading" style={{ marginLeft: 1 + 'em' }} >{name}</h4>
                <p style={{ marginLeft: 2 + 'em' }}>
                    <b >Price: </b>
                    {price}
                </p>
                <p style={{ marginLeft: 2 + 'em' }}>
                    <b >Amount: </b>
                    {amount}
                </p>
                <p style={{ marginLeft: 2 + 'em' }}>
                    <button onClick={addCartPopover}>Add to cart</button>
                </p>
                <hr />
            </div>
        </li>
    );
};

function addCartPopover() {
    alert("Great Shot!");
}

const myelement = (
    <button onClick={addCartPopover}>Take the shot!</button>
);

const popover = (
    <Popover id="popover-basic">
        <Popover.Title as="h3">Popover right</Popover.Title>
        <Popover.Content>
            And here's some <strong>amazing</strong> content. It's very engaging.
      right?
    </Popover.Content>
    </Popover>
);

const Example = () => (
    <OverlayTrigger trigger="click" placement="right" overlay={popover}>
        <button variant="success">Click me to see</button>
    </OverlayTrigger>
);

export default ItemDetail;
