import React, { Component } from 'react';
import { Button, UncontrolledPopover, PopoverHeader, PopoverBody } from 'reactstrap';

const ItemDetail = props => {
    const { name, price, amount, imageUrl } = props.item
    const baseApiUrl = 'http://localhost:3000';

    var imageUrlFull = baseApiUrl.concat(imageUrl);

    return (
        <li className="media">
            <div className="media-left">
                <img
                    className="media-object img-circle"
                    width="150px"
                    src={imageUrlFull}
                    alt={imageUrl}
                />
            </div>
            <div className="media-right">
                <h4 className="media-heading" style={{ marginLeft: 1 + 'em' }} >{name}</h4>
                <p style={{ marginLeft: 2 + 'em' }}>
                    <b >Price: </b>
                    {price}
                </p>
                <p style={{ marginLeft: 2 + 'em' }}>
                    <b >Amount: </b>
                    {amount}
                </p>
                <p style={{ marginLeft: 2 + 'em' }}>
                    <button onClick={addCartPopover}>Take the shot!</button>
                    <UncontrolledPopover placement="bottom" target="UncontrolledPopover">
                        <PopoverHeader>Popover Title</PopoverHeader>
                        <PopoverBody>Sed posuere consectetur est at lobortis. Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum.</PopoverBody>
                    </UncontrolledPopover>
                </p>
                <hr />
            </div>
        </li>
    );
};

function addCartPopover() {
    alert("Great Shot!");
}

const myelement = (
    <button onClick={addCartPopover}>Take the shot!</button>
);

export default ItemDetail;
