import React, { Component } from 'react';
import ItemDetail from './ItemDetail';
import * as ItemApi from './ItemApi';
import { UncontrolledPopover, PopoverHeader, PopoverBody } from 'reactstrap';


class ItemList extends Component {
    constructor() {
        super();

        this.state = {
            items: []
        };
    }

    componentDidMount() {
        //this.setState({ items: this.getItems() });

        ItemApi.getItemsDb()
        .then(items => {
            this.setState({ items });
            console.log('Success : Items retreived from Database ');
         })
        .catch(error => {
            console.log('Error : could not retreived Items from Database', error);
         });
    }

    render() {
        const { items } = this.state;

        return (
            <ul className="media-list">
                {items.map(item => (
                    <ItemDetail key={item._id} item={item} />
                ))}
                <p style={{ marginLeft: 2 + 'em' }}>
                    <button id="PopoverLegacy" type="button"> Add to cart </button>
                    <UncontrolledPopover trigger="legacy" placement="right" target="PopoverLegacy">
                        <PopoverHeader>{name}</PopoverHeader>
                        <PopoverBody>
                            <img
                                className="media-object img-circle"
                                width="150px"
                                src={imageUrlFull}
                                alt={name}
                            />
                            <p>
                                {description}
                            </p>
                        </PopoverBody>
                    </UncontrolledPopover>
                </p>
            </ul>
        );
    }
}

export default ItemList;
